    def table_btn_clicked(self, i:int):
        if self.cur_gamer == 'X':
            self.lstBtn[i].setText('X')
            self.lstBtn[i].setEnabled(False)
            self.cur_gamer = 'O'
            self.lblX.setStyleSheet(Style.none_active())
            self.lblO.setStyleSheet(Style.active_style())
        else:
            self.lstBtn[i].setText('O')
            self.lstBtn[i].setEnabled(False)
            self.cur_gamer = 'X'
            self.lblO.setStyleSheet(Style.none_active())
            self.lblX.setStyleSheet(Style.active_style())
        
        ans = self.game.check()
        if ans != None:
            QMessageBox.information(self,"Golib",f"{ans} siz golib buldingiz")
            self.reset_btn()
            
    def reset_btn(self):
        for btn in self.lstBtn:
            btn.setText("")
            btn.setEnabled(True)
        
